import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { useRouter } from 'next/router';
import { Card, CardHeader, CardContent, CardMedia, Avatar, Typography, CardActions, Button } from '@material-ui/core';
import { ArrowRightAlt } from '@material-ui/icons';
import { Skeleton } from '@material-ui/lab';
import { generateBlogPostDate } from '../../../utilities/helper-methods';
import * as link from '../../../utilities/link-config';

const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex',
        flexDirection: 'row',
        justifyContent: 'center',
        width: '100%',
    },
    blogWrapper: {
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        minWidth: '100%',
        [theme.breakpoints.up('500')]: {
            flexDirection: 'row',
            justifyContent: 'flex-start',
            flexWrap: 'wrap'
        },
    },
    card: {
        [theme.breakpoints.up('xs')]: {
            margin: '0 auto',
            marginBottom: theme.spacing(2),
            width: '90%',
            // flex: '1 1 90%',
            display: 'flex',
            flexDirection: 'column',
            justifyContent: 'space-between'
        },
        [theme.breakpoints.up('550')]: {
            margin: theme.spacing(1),
            width: '46%',
            // flex: '1 1 46%'
        },
        [theme.breakpoints.up('800')]: {
            width: '31%',
            // flex: '1 1 31%'
        },
        [theme.breakpoints.up('lg')]: {
            width: '23%',
            // flex: '1 1 23%'
        },
    },
    media: {
        height: 190,
    },
    tag: {
        padding: theme.spacing(0.5, 1, 0.5, 1),
        color: theme.palette.common.white
    },
    tagWrapper: {
        cursor: 'pointer',
        backgroundColor: theme.palette.primary.main,
        display: 'inline-block',
        marginRight: theme.spacing(0.5),
        marginTop: theme.spacing(0.5),
        borderRadius: theme.spacing(0.5)
    },
    title: {
        marginTop: theme.spacing(1),
        fontWeight: 700,
        color: theme.palette.primary.light,
        lineHeight: 1
    },
    buttonWrapper: {
        display: 'flex',
        flexDirection: 'row',
        justifyContent: 'flex-end'

    }
}));

export default function Preview(props) {
    const classes = useStyles();
    const router = useRouter();


    const loading = props.loading
    const article = props.post

    const redirect = (id) => {
        router.push(`blogpost/${id}`)
    }

    return (
        <div className={classes.root}>
            <Card className={classes.card}>
                <div>
                    <CardHeader
                        avatar={
                            loading
                                ?
                                <Skeleton animation="wave" variant="circle" width={40} height={40} />
                                :
                                <Avatar
                                    alt={`${article.author}_avatar`}
                                    src='/assets/icons/orange-world.png'
                                />
                        }
                        title={
                            loading
                                ?
                                <Skeleton animation="wave" height={10} width="80%" style={{ marginBottom: 6 }} />
                                : article.author
                        }
                        titleTypographyProps={{
                            color: 'primary',
                        }}
                        subheader={
                            loading
                                ? <Skeleton animation="wave" height={10} width="40%" />
                                : generateBlogPostDate(article.created_at)
                        }
                    />
                    {
                        loading
                            ?
                            <Skeleton animation="wave" variant="rect" className={classes.media} />
                            :
                            <CardMedia
                                className={classes.media}
                                image={`${link.STRAPI_URL}${article.titleImage.url}`}
                                title={article.titleImage.name}
                            />
                    }
                    <CardContent>
                        {
                            loading
                                ?
                                <React.Fragment>
                                    <Skeleton animation="wave" height={10} style={{ marginBottom: 6 }} />
                                    <Skeleton animation="wave" height={10} width="80%" />
                                </React.Fragment>
                                :
                                <React.Fragment>
                                    {
                                        article.categories.map((tag, index) => (
                                            <div value={tag.name} id={index} key={`${tag}-${index}`} className={classes.tagWrapper}>
                                                <Typography variant="button" className={classes.tag}>
                                                    {tag.name}
                                                </Typography>
                                            </div>
                                        ))
                                    }
                                    <Typography className={classes.title} variant="h6">
                                        {article.title}
                                    </Typography>
                                </React.Fragment>
                        }
                    </CardContent>
                </div>
                <CardActions className={classes.buttonWrapper}>
                    {
                        loading
                            ? <Skeleton animation="wave" width={93} height={29} style={{ marginBottom: 6 }} />
                            :
                            <Button
                                size="small"
                                color="primary"
                                endIcon={<ArrowRightAlt />}
                                onClick={() => redirect(article.id)}>
                                Read more
                    </Button>
                    }
                </CardActions>
            </Card>
        </div>

    );
}
