import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { useRouter } from 'next/router';
import { Card, CardHeader, CardContent, CardMedia, Avatar, Typography, CardActions, Button } from '@material-ui/core';
import { ArrowRightAlt } from '@material-ui/icons';
import { Skeleton } from '@material-ui/lab';
import { generateBlogPostDate } from '../../../utilities/helper-methods';
import * as link from '../../../utilities/link-config';

const useStyles = makeStyles((theme) => ({
    root: {
        [theme.breakpoints.up('xs')]: {},
        [theme.breakpoints.up('sm')]: {},
        [theme.breakpoints.up('md')]: {},
        [theme.breakpoints.up('lg')]: {},
        [theme.breakpoints.up('xl')]: {}
    }
}));

export default function Preview(props) {
    const classes = useStyles();
    const router = useRouter();

    const loading = props.loading
    const article = props.post

    const redirect = (id) => {
        router.push(`blogpost/${id}`)
    }

    return (
        <div className={classes.root}>
            <Card className={classes.card}>
                <div>
                    <CardHeader
                        avatar={
                            loading
                                ?
                                <Skeleton animation="wave" variant="circle" width={40} height={40} />
                                :
                                <Avatar
                                    alt={`${article.author}_avatar`}
                                    src='/assets/icons/orange-world.png'
                                />
                        }
                        title={
                            loading
                                ?
                                <Skeleton animation="wave" height={10} width="80%" style={{ marginBottom: 6 }} />
                                : article.author
                        }
                        titleTypographyProps={{
                            color: 'primary',
                        }}
                        subheader={
                            loading
                                ? <Skeleton animation="wave" height={10} width="40%" />
                                : generateBlogPostDate(article.created_at)
                        }
                    />
                    {/* {
                        loading
                            ?
                            <Skeleton animation="wave" variant="rect" className={classes.media} />
                            :
                            <CardMedia
                                className={classes.media}
                                image={`${link.STRAPI_URL}${article.titleImage.url}`}
                                title={singleArticle.titleImage.name}
                            />
                    } */}
                    <CardContent>
                        {
                            loading
                                ?
                                <React.Fragment>
                                    <Skeleton animation="wave" height={10} style={{ marginBottom: 6 }} />
                                    <Skeleton animation="wave" height={10} width="80%" />
                                </React.Fragment>
                                :
                                <React.Fragment>
                                    {
                                        article.tags.map((tag, index) => (
                                            <div value={tag.name} id={index} key={`${tag}-${index}`} className={classes.tagWrapper}>
                                                <Typography variant="button" className={classes.tag}>
                                                    {tag.name}
                                                </Typography>
                                            </div>
                                        ))
                                    }
                                    <Typography className={classes.title} variant="h6">
                                        {article.title}
                                    </Typography>
                                </React.Fragment>
                        }
                    </CardContent>
                </div>
                <CardActions className={classes.buttonWrapper}>
                    {
                        loading
                            ? <Skeleton animation="wave" width={93} height={29} style={{ marginBottom: 6 }} />
                            :
                            <Button
                                size="small"
                                color="primary"
                                endIcon={<ArrowRightAlt />}
                                onClick={() => redirect(props.id)}>
                                Read more
                    </Button>
                    }
                </CardActions>
            </Card>
            <div className={classes.blogWrapper}>
                {
                    props.articles !== undefined
                        ?
                        props.articles.map((singleArticle, index) => (
                            <Article
                                tagClicked={tagClicked}
                                key={index}
                                article={{
                                    id: singleArticle.id,
                                    author: singleArticle.author,
                                    avatar: '/assets/icons/orange-world.png',
                                    title: singleArticle.title,
                                    date: generateBlogPostDate(singleArticle.created_at),
                                    imageSrc: `${link.STRAPI_URL}${singleArticle.titleImage.url}`,
                                    imageAlt: singleArticle.titleImage.name,
                                    tags: singleArticle.categories
                                }}
                            />
                        ))
                        : <Article loading />
                }
            </div>
        </div>

    );
}
